import React, { useState } from 'react';
import axios from 'axios';

function NewBot() {
  const [botName, setBotName] = useState('');
  const [description, setDescription] = useState('');
  const [tradeType, setTradeType] = useState('live');
  const [customizations, setCustomizations] = useState({
    x_api: { crypto: false, general: false, sentiment: false },
    ask_news: false
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    const payload = {
      bot_name: botName,
      description,
      trade_type: tradeType,
      customizations: customizations
    };
    axios.post('http://localhost:5000/api/bots', payload)
      .then(response => {
        alert("Bot created successfully!");
      })
      .catch(error => {
        console.error("Error creating bot:", error);
      });
  };

  const toggleXAPIOption = (option) => {
    setCustomizations(prevState => ({
      ...prevState,
      x_api: {
        ...prevState.x_api,
        [option]: !prevState.x_api[option]
      }
    }));
  };

  return (
    <div>
      <h2>Create New Bot</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Bot Name:</label>
          <input type="text" value={botName} onChange={(e) => setBotName(e.target.value)} required />
        </div>
        <div>
          <label>Description:</label>
          <textarea value={description} onChange={(e) => setDescription(e.target.value)}></textarea>
        </div>
        <div>
          <label>Trade Type:</label>
          <select value={tradeType} onChange={(e) => setTradeType(e.target.value)}>
            <option value="live">Live Trading</option>
            <option value="backtesting">Backtesting</option>
          </select>
        </div>
        <div>
          <label>Integrate X API:</label>
          <div>
            <label>
              <input 
                type="checkbox" 
                checked={customizations.x_api.crypto} 
                onChange={() => toggleXAPIOption('crypto')} 
              />
              Crypto News
            </label>
            <label>
              <input 
                type="checkbox" 
                checked={customizations.x_api.general} 
                onChange={() => toggleXAPIOption('general')} 
              />
              General News
            </label>
            <label>
              <input 
                type="checkbox" 
                checked={customizations.x_api.sentiment} 
                onChange={() => toggleXAPIOption('sentiment')} 
              />
              Sentiment Analysis
            </label>
          </div>
        </div>
        <div>
          <label>
            <input 
              type="checkbox" 
              checked={customizations.ask_news}
              onChange={() => setCustomizations(prevState => ({...prevState, ask_news: !prevState.ask_news}))}
            />
            Integrate Ask News
          </label>
        </div>
        <button type="submit">Create Bot</button>
      </form>
    </div>
  );
}

export default NewBot;
